import React, { useState } from 'react';
import { QueryItem } from '../../types/api';
import './QueryCard.css';

interface QueryCardProps {
  query: QueryItem;
  className?: string;
}

const QueryCard: React.FC<QueryCardProps> = ({ query, className = '' }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(query.query);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <div className={`query-card ${className}`}>
      <div className="query-header">
        <h3 className="query-title">Query #{query.id}</h3>
        <div className="query-actions">
          <button
            onClick={handleCopy}
            className={`copy-btn ${copied ? 'copied' : ''}`}
          >
            {copied ? 'Copied!' : 'Copy'}
          </button>
        </div>
      </div>

      {query.description && (
        <p className="query-description">{query.description}</p>
      )}

      <div className="query-sql">
        <code>{query.query}</code>
      </div>

      <div className="query-meta">
        <div className="query-stat">
          <span className="query-stat-icon">üóÇÔ∏è</span>
          <span>{query.tables.length} tables</span>
        </div>
        <div className="query-stat">
          <span className="query-stat-icon">üîó</span>
          <span>{query.joins.length} joins</span>
        </div>
      </div>

      {query.tags.length > 0 && (
        <div className="query-tags">
          {query.tags.map((tag, index) => (
            <span key={index} className="query-tag">
              {tag}
            </span>
          ))}
        </div>
      )}
    </div>
  );
};

export default QueryCard;
