import React from 'react';
import { Message } from '../../types/api';
import './MessageBubble.css';

interface MessageBubbleProps {
  message: Message;
}

const MessageBubble: React.FC<MessageBubbleProps> = ({ message }) => {
  const isUser = message.role === 'user';

  const formatDate = (date: Date) => {
    return new Date(date).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className={`message-bubble ${isUser ? 'user' : 'assistant'}`}>
      <div className="message-content">
        <div className="message-text">
          {message.content.split('\n').map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
        </div>

        {message.sqlQuery && (
          <div className="sql-query">
            <h4>Generated SQL:</h4>
            <pre>
              <code>{message.sqlQuery}</code>
            </pre>
          </div>
        )}

        {message.sqlResult && (
          <div className="sql-result">
            <h4>Query Results:</h4>
            <div className="result-data">
              {JSON.stringify(message.sqlResult, null, 2)}
            </div>
          </div>
        )}

        {message.sources && message.sources.length > 0 && (
          <div className="sources">
            <h4>Sources:</h4>
            <ul>
              {message.sources.map((source, index) => (
                <li key={index}>
                  <span className="source-score">Score: {source.score.toFixed(2)}</span>
                  <p>{source.content}</p>
                </li>
              ))}
            </ul>
          </div>
        )}

        {message.tokenUsage && (
          <div className="token-usage">
            <span>Tokens: {message.tokenUsage.total}</span>
            {message.contextUtilization && (
              <span>Context: {(message.contextUtilization * 100).toFixed(1)}%</span>
            )}
          </div>
        )}
      </div>

      <div className="message-meta">
        <span className="message-time">{formatDate(message.timestamp)}</span>
        {message.agentUsed && (
          <span className="agent-badge">{message.agentUsed}</span>
        )}
        {message.isStreaming && (
          <span className="streaming-indicator">‚óè</span>
        )}
      </div>
    </div>
  );
};

export default MessageBubble;
