import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState, AppDispatch } from '../../store';
import { fetchSchema } from '../../store/dataSlice';
import { TableSchema, Column } from '../../types/api';
import LoadingSpinner from '../common/LoadingSpinner';
import './SchemasTree.css';

const SchemasTree: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const { schema, isLoading } = useSelector((state: RootState) => state.data);
  const [expandedTables, setExpandedTables] = useState<Set<string>>(new Set());
  const [selectedTable, setSelectedTable] = useState<string | null>(null);

  React.useEffect(() => {
    dispatch(fetchSchema());
  }, [dispatch]);

  const toggleTable = (tableName: string) => {
    const newExpanded = new Set(expandedTables);
    if (newExpanded.has(tableName)) {
      newExpanded.delete(tableName);
    } else {
      newExpanded.add(tableName);
    }
    setExpandedTables(newExpanded);
  };

  const selectTable = (tableName: string) => {
    setSelectedTable(tableName);
  };

  if (isLoading && schema.length === 0) {
    return <LoadingSpinner message="Loading database schema..." />;
  }

  return (
    <div className="schemas-tree">
      <div className="tree-header">
        <h3>Database Schema</h3>
        <button 
          onClick={() => dispatch(fetchSchema())}
          className="refresh-btn"
          disabled={isLoading}
        >
          {isLoading ? 'Loading...' : 'Refresh'}
        </button>
      </div>

      {schema.length === 0 ? (
        <div className="empty-state">
          <p>No schema data available</p>
        </div>
      ) : (
        <div className="schema-list">
          {schema.map((table) => (
            <div key={table.name} className="table-node">
              <div 
                className={`table-header ${selectedTable === table.name ? 'selected' : ''}`}
                onClick={() => toggleTable(table.name)}
              >
                <span className="expand-icon">
                  {expandedTables.has(table.name) ? 'â–¼' : 'â–¶'}
                </span>
                <span 
                  className="table-name"
                  onClick={(e) => {
                    e.stopPropagation();
                    selectTable(table.name);
                  }}
                >
                  ðŸ“‹ {table.name}
                </span>
                <span className="column-count">({table.columns.length})</span>
              </div>

              {expandedTables.has(table.name) && (
                <div className="columns-container">
                  {table.columns.map((column) => (
                    <div key={column.name} className="column-row">
                      <span className="column-icon">ðŸ”¹</span>
                      <span className="column-name">{column.name}</span>
                      <span className="column-type">{column.type}</span>
                      {column.nullable && (
                        <span className="nullable-badge">NULL</span>
                      )}
                      {column.description && (
                        <span className="column-description">{column.description}</span>
                      )}
                    </div>
                  ))}

                  {table.relationships.length > 0 && (
                    <div className="relationships">
                      <h4>Relationships:</h4>
                      {table.relationships.map((rel, index) => (
                        <div key={index} className="relationship">
                          <span className="relationship-type">{rel.type}</span>
                          <span>
                            {rel.sourceTable}.{rel.sourceColumn} â†’ {rel.targetTable}.{rel.targetColumn}
                          </span>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {selectedTable && (
        <div className="selected-table-info">
          <h4>Selected: {selectedTable}</h4>
          <p>Click on columns to explore relationships and build queries.</p>
        </div>
      )}
    </div>
  );
};

export default SchemasTree;
