import { createSlice, createAsyncThunk, PayloadAction } from '@reduxjs/toolkit';
import { Message, Conversation, ChatRequest } from '../types/api';
import { chatService } from '../services/chatService';

interface ChatState {
  conversations: Record<string, Conversation>;
  currentSessionId: string | null;
  isLoading: boolean;
  error: string | null;
  agentType: string;
  messages: Message[];
}

const initialState: ChatState = {
  conversations: {},
  currentSessionId: null,
  isLoading: false,
  error: null,
  agentType: 'normal',
  messages: [],
};

export const sendMessage = createAsyncThunk(
  'chat/sendMessage',
  async (request: ChatRequest) => {
    const response = await chatService.sendMessage(request);
    return response;
  }
);

export const executeSQL = createAsyncThunk(
  'chat/executeSQL',
  async (request: { sql: string; dryRun?: boolean }) => {
    const response = await chatService.executeSQL(request.sql, request.dryRun);
    return response;
  }
);

const chatSlice = createSlice({
  name: 'chat',
  initialState,
  reducers: {
    setAgentType: (state, action: PayloadAction<string>) => {
      state.agentType = action.payload;
    },
    setCurrentSession: (state, action: PayloadAction<string>) => {
      state.currentSessionId = action.payload;
    },
    addMessage: (state, action: PayloadAction<Message>) => {
      state.messages.push(action.payload);
    },
    clearMessages: (state) => {
      state.messages = [];
    },
    clearError: (state) => {
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(sendMessage.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(sendMessage.fulfilled, (state, action) => {
        state.isLoading = false;
        state.messages.push(action.payload);
      })
      .addCase(sendMessage.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.error.message || 'Failed to send message';
      })
      .addCase(executeSQL.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(executeSQL.fulfilled, (state, action) => {
        state.isLoading = false;
        // Handle SQL execution results
      })
      .addCase(executeSQL.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.error.message || 'Failed to execute SQL';
      });
  },
});

export const { setAgentType, setCurrentSession, addMessage, clearMessages, clearError } = chatSlice.actions;
export default chatSlice.reducer;
