import { api } from './api';
import { QueryItem, TableSchema } from '../types/api';

class DataService {
  async getSchema(): Promise<TableSchema[]> {
    const response = await api.get<TableSchema[]>('/api/data/schema');
    return response;
  }

  async getQueries(params: {
    search?: string;
    page?: number;
    pageSize?: number;
    filters?: any;
  }): Promise<{ queries: QueryItem[]; total: number }> {
    const queryParams = new URLSearchParams();

    if (params.search) queryParams.append('search', params.search);
    if (params.page) queryParams.append('page', params.page.toString());
    if (params.pageSize) queryParams.append('page_size', params.pageSize.toString());
    if (params.filters) queryParams.append('filters', JSON.stringify(params.filters));

    const endpoint = `/api/data/queries${queryParams.toString() ? `?${queryParams.toString()}` : ''}`;
    const response = await api.get<{ queries: QueryItem[]; total: number }>(endpoint);
    return response;
  }

  async getAnalytics(): Promise<any> {
    const response = await api.get('/api/data/analytics');
    return response;
  }

  async getTableDetails(tableName: string): Promise<TableSchema> {
    const response = await api.get<TableSchema>(`/api/data/table/${tableName}`);
    return response;
  }
}

export const dataService = new DataService();
