import { api } from './api';
import { ChatRequest, ChatResponse, SQLExecuteResponse } from '../types/api';

class ChatService {
  async sendMessage(request: ChatRequest): Promise<ChatResponse> {
    const response = await api.post<ChatResponse>('/api/chat/query', request);
    return response;
  }

  async executeSQL(sql: string, dryRun: boolean = false): Promise<SQLExecuteResponse> {
    const response = await api.post<SQLExecuteResponse>('/api/chat/execute-sql', {
      sql,
      dry_run: dryRun,
    });
    return response;
  }

  async getChatHistory(sessionId: string) {
    const response = await api.get(`/api/chat/history/${sessionId}`);
    return response;
  }

  createWebSocketConnection(sessionId: string): WebSocket {
    const wsUrl = `${process.env.REACT_APP_WS_URL || 'ws://localhost:8000'}/ws/chat/${sessionId}`;
    return new WebSocket(wsUrl);
  }
}

export const chatService = new ChatService();
